import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch,Link } from "react-router-dom";
import "./dashboard.css"
import axios from "axios"
import "./admin.css";
class ListAdmin extends Component {
	constructor(){
		super()
		this.state={
			habitant:[],
			search:"",
			nom:"",
			password:"",
			quartier:""
		}
		this.searchChange= this.searchChange.bind(this)
		this.handleChange= this.handleChange.bind(this)

	}
	searchChange(e){
		this.setState({search:e.target.value})
	}
	handleChange(e){
		this.setState({[e.target.name]: e.target.value })
	}
	componentDidMount(){
		axios.get("https://fokotany.herokuapp.com/user").then(user=>{
			console.log(user.data)
			this.setState({habitant:user.data})
		})
		if(localStorage.getItem("admin")!== null){
			document.querySelector(".admin").style.display="none"
		}
	}
	// handleConnect(e){
 //    e.preventDefault()
 //    console.log("connect")

	// 	axios.get("http://localhost:8080/user").then(user=>{
	// 		console.log(user.data)
	// 		this.setState({habitant:user.data})
	// 	})
 //   var tab=[]
 //     for(let i=0;i<admin.length;i++){
 //     	if(admin[i].nom==this.state.nom && admin[i].quartier==this.state.quartier && admin[i].password==this.state.password){
 //     		tab.push(admin[i])
 //     	}

 //     }
 //     if(tab.length>0){
 //     	localStorage.setItem("admin",this.state.quartier)
 //     	document.querySelector(".admin").style.display="none"
 //     }
 //     else{
 //     	 alert("misy tsy mety")
 //     }


 //  }
	render(){
		 const admin= [
    {
    	_id:0,
    	nom:"RAKOTO",
    	quartier:"Ambatomaro",
    	numero:"Ambatomaro",
    	photo:"https://fokotany.herokuapp.com/public/init.jpg",
    },
    {
    	_id:1,
    	nom:"RABE",
    	quartier:"Ambomirary",
    	numero:"Ambomirary",
    	photo:"https://fokotany.herokuapp.com/public/init.jpg",
    }
    ] 
		return(
		<Router>
			    <div id="page-content-wrapper">

			      <nav class="navbar navbar-expand-lg navbar-light bg-light border-bottom">
			        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
			          <span class="navbar-toggler-icon"></span>
			        </button>
			        <div class="collapse navbar-collapse" id="navbarSupportedContent">
			          <ul class="navbar-nav ml-auto mt-2 mt-lg-0">
			            <li class="nav-item active">
			              <input type="search" placeholder="Votre recherche"value={this.state.search} onChange={this.searchChange}/>
			            </li>
			            <li class="nav-item">

			            </li>
			          </ul>
			        </div>
			       </nav>

			       <div class="container-fluid">
			        <table class="table">
					  <thead>
					    <tr>
					      <th scope="col">Id</th>
					      <th scope="col">Photo</th>
					      <th scope="col">Nom</th>
					      <th scope="col">quartier</th>
					    </tr>
					  </thead>
					  <tbody>
					  {admin.length > 0 ? (admin.map(user=>{

					  	return(
					  			<tr>
							      <th scope="row">{user._id}</th>
							      <td><img width="100"src={user.photo}/></td>
							      <td>{user.nom}</td>
							      <td>{user.numero}</td>
					    		</tr>
					  		)

					  })) 

					  	:""}

					  </tbody>
					</table>
			      </div>

		   </div>
		</Router>



		)
	}
}
export default ListAdmin
