import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import jsPDF from 'jspdf';
import $ from 'jquery'
import domtoimage from 'dom-to-image';
import axios from "axios"
import "./App.css";
import "./acceuil.css";
import "./login.css";
import "./photo.css";
import "./deconnect.css";
import "./residence.css";
import  "dom-to-image/dist/dom-to-image.min.js"
import { saveAs } from 'file-saver'
import Dashboard from "./components/Dashboard"
// import "./font-awesome.min.css"

// Check for token to keep user logged in

class App extends Component {
  constructor(){
    super()
    this.state={
      connect:false,
      username:"",
      nom:"",
      prenom:"",
      birthday:"",
      ancquartier:"",
      nouvquartier:"",
      adresse:"",
      telephone:"",
      password:"",
      password_confirm:"",
      profil:[],
      src:""
    }
     this.handleChange= this.handleChange.bind(this)
    this.handleSubmit= this.handleSubmit.bind(this)
     this.handleConnect= this.handleConnect.bind(this)
      this.handleConnectRes= this.handleConnectRes.bind(this)
     this.handleDeconnect= this.handleDeconnect.bind(this)
     this.upload= this.upload.bind(this)
  }
  handleChange(e){

    this.setState({ [e.target.name]: e.target.value });

  }
  handleSubmit(e){
    e.preventDefault()
   const data = new FormData();
    data.append('nom', this.state.nom);
    data.append('prenom', this.state.prenom);
    data.append('password_confirm', this.state.password_confirm);
    data.append('birthday', this.state.birthday);
    data.append('ancquartier', this.state.ancquartier);
    data.append('nouvquartier', this.state. nouvquartier);
    data.append('adresse', this.state.adresse);
    data.append('telephone', this.state.telephone);
    data.append('password', this.state.password);
     data.append('pseudo', this.state.pseudo);
     axios.post("http://localhost:8080/register",data).
    then(res=>{
      console.log(res.data)
      localStorage.setItem("_id",res.data._id)
    })
    document.querySelector(".register").style.display="none"
    document.querySelector(".photo").style.display="block"
    document.querySelector(".photo").style.top="0"
    document.querySelector(".inscription5").style.display="block"
  }
  componentDidMount(){
    axios.get("http://localhost:8080/user/"+localStorage.getItem("id")).then(res=>{
      this.setState({profil:res.data})
       this.setState({username:res.data.nom})
      this.setState({connect: localStorage.getItem("connect")})
       console.log(this.state.connect)
    })
      axios.get("http://localhost:8080/user/"+localStorage.getItem("_id")).then(res=>{
      this.setState({src:"http://localhost:8080/public/"+res.data.image})
    })
  }
  upload(e){
    e.preventDefault()
   const data = new FormData();
    data.append('image', this.uploadInput.files[0]);

     axios.post("http://localhost:8080/photo/"+ localStorage.getItem("_id"),data).
    then(res=>{
      console.log(res.data)
       axios.get("http://localhost:8080/user/"+localStorage.getItem("_id")).then(res=>{
      this.setState({src:"http://localhost:8080/public/"+res.data.image})
    })
    })
  }

  handleConnect(e){
    e.preventDefault()
    console.log("connect")
    const individu= {
                    pseudo: this.state.pseudo,
                    password: this.state.password
                }
    axios.post("http://localhost:8080/login",individu).
    then(res=>{
      console.log(res.data)
       if(res.data=='User not found'){
        document.querySelector(".erreuruser").innerHTML="utilisateur introuvable"  
         }
      else if(res.data!=='User not found'&& res.data=='Incorrect Password'){
        document.querySelector(".erreuruser").innerHTML="" 
        document.querySelector(".erreurpassword").innerHTML="mot de passe incorrect"  
         }
     if(res.data!=='Incorrect Password' && res.data!=='User not found' ){
       document.querySelector(".erreurpassword").innerHTML="" 
    localStorage.setItem("id",res.data.id)
    localStorage.setItem("nom",res.data.nom)
    localStorage.setItem("connect",res.data.success)
    axios.get("http://localhost:8080/user/"+localStorage.getItem("id")).then(res=>{
      this.setState({profil:res.data})
      this.setState({connect: localStorage.getItem("connect")})
      this.setState({username:res.data.nom})
      console.log(this.state.connect)
    })
     this.setState({connect:true})
    document.querySelector(".connect").style.display="none"
      }


    })


  }
   handleConnectRes(e){
    e.preventDefault()
    console.log("connect")
    const individu= {
                    pseudo: this.state.pseudo,
                    password: this.state.password
                }
    axios.post("http://localhost:8080/login",individu).
    then(res=>{
    localStorage.setItem("id",res.data.id)
    localStorage.setItem("nom",res.data.nom)
    localStorage.setItem("connect",res.data.success)
    axios.get("http://localhost:8080/user/"+localStorage.getItem("id")).then(res=>{
    console.log(res.data)
    this.setState({profil:res.data})
    this.setState({connect: localStorage.getItem("connect")})
    })
    })
     this.setState({connect:true})
     document.querySelector(".connect").style.display="none"
     document.querySelector(".residence").style.display="block"
     document.querySelector(".residence").style.top="0"
     document.querySelector(".inscription2").style.display="block"

  }
  handleDeconnect(e){
    e.preventDefault()
    localStorage.removeItem("id")
    localStorage.removeItem("nom")
    localStorage.removeItem("connect")
    this.setState({connect:false})
    document.querySelector(".deconnect").style.display="none"
  }
  render() {

   let sty={
    color:"white"
   }
   let styl={
    background:"linear-gradient(40deg,#c700fa,#eb2af3,#d793c7)"
   }
    function PDF1(e){
      e.preventDefault()
      var doc = new jsPDF();
      var elementHandler = {
        '#ignorePDF': function (element, renderer) {
          return true;
        }
      };
    //   domtoimage.toBlob(document.querySelector(".residence"))
    // .then(function (blob) {
    //     saveAs(blob, 'my-node.png');
    //     doc.addImage('my-node.png', 'PNG', 15, 40, 180, 160);
    //     doc.save("datauri.pdf");
    // });
      domtoimage.toPng(document.querySelector(".residence")).then(function (dataUrl) {
        var img = new Image();
        img.src = dataUrl;
        console.log(dataUrl)
        var imdata=dataUrl
        doc.addImage(imdata, 'PNG', 15, 40, 180, 160);
        doc.save("datauri.pdf");
        // document.querySelector(".inscription2").innerHTML+=` <img src={dataUrl}/>`
        // document.body.appendChild(img);
    }).catch(function (error) {
        console.error('oops, something went wrong!', error);
    });
   }


    var video = document.getElementById('video');
      function play(){
        if(navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
 navigator.mediaDevices.getUserMedia({ video: true }).then(function(stream) {
 video.srcObject = stream;
 video.play();
 });
      }
}
 function clone(){
    var canvas1 = document.getElementById('cvs').getContext('2d');
    canvas1.drawImage(video, 0,0, 150, 112);
    var base64=document.getElementById('cvs').toDataURL("image/png"); //l'image au format base 64
    console.log(base64);
    document.getElementById('tar').value='';
    document.getElementById('tar').value=base64;
    video.upload();
  }

  // window.onload = init;

function stop(e) {
  var stream = video.srcObject;
  var tracks = stream.getTracks();

  for (var i = 0; i < tracks.length; i++) {
    var track = tracks[i];
    track.stop();
  }

  video.srcObject = null;
}

    return (
    <Router>
      <div>
          <div class=" navbar container-fluid" style={styl}>
            <div class="logo">
                <i class="fa fa-xing" style={sty} ></i>
                <span class="zion">Service Fokotany</span> 

            </div>
            <div class="connection"> 
              {this.state.connect ? (
             <div className="user"> 
             <select> 
              <option> {localStorage.getItem("nom")} </option> 
              <option>Parametre</option> 
              <option onClick={(e)=> { 
