import React, { Component } from "react";
import "./photo.css";
import "./acceuil.css";
import axios from "axios"
  class Register extends Component {
    constructor(){
    super()
    this.state={
      switch:false,
      connect:false,
      username:"",
      pseudo:"",
      nom:"",
      prenom:"",
      birthday:"",
      lieu:"",
      metier:"",
      sex:"",
      ancquartier:"",
      nouvquartier:"",
      adresse:"",
      telephone:"",
      password:"",
      password_confirm:"",
      profil:[],
      src:""
    }
    this.handleChange= this.handleChange.bind(this)
    this.handleChange1= this.handleChange1.bind(this)
    this.handleChangeQuartier1=this.handleChangeQuartier1.bind(this)
    this.handleChangeQuartier2=this.handleChangeQuartier2.bind(this)
    this.handleChangemetier=this.handleChangemetier.bind(this)
    this.handleSubmit= this.handleSubmit.bind(this)
  }
    handleChange(e){
    this.setState({ [e.target.name]: e.target.value }); 
  }
   handleChange1(e){
    e.preventDefault()
    this.setState({lieu:document.getElementById('lieu').child.innerHTML});
   document.getElementById('lieubut').innerHTML=this.state.lieu

  }
  handleChangeQuartier1(e){
    e.preventDefault()
    this.setState({ancquartier:document.getElementById('ancquartier').child.innerHTML});
    document.getElementById('ancquartierbut').innerHTML=this.state.ancquartier
  }
   handleChangeQuartier2(e){
    e.preventDefault()
    this.setState({nouvquartier:document.getElementById('nouvquartier').child.innerHTML});
    document.getElementById('nouvquartierbut').innerHTML=this.state.nouvquartier
  }
   handleChangemetier(e){
    e.preventDefault()
    this.setState({metier:document.getElementById('metier').child.innerHTML});
    document.getElementById('metierbut').innerHTML=this.state.metier
  }
  componentDidMount(){
    axios.get("http://localhost:8080/user/"+localStorage.getItem("id")).then(res=>{
      this.setState({profil:res.data})
       this.setState({username:res.data.nom})
      this.setState({connect: localStorage.getItem("connect")})
       console.log(this.state.connect)
    })
  }
  handleSubmit(e){
    e.preventDefault()
   const data = new FormData();
    data.append('nom', this.state.nom);
    data.append('prenom', this.state.prenom);
    data.append('password_confirm', this.state.password_confirm);
    data.append('birthday', this.state.birthday);
    data.append('lieu', this.state.lieu);
    data.append('metier', this.state.metier);
