import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import jsPDF from 'jspdf';
import $ from 'jquery'
//import Navbar from "./components/menu";
import "./App.css";

// Check for token to keep user logged in

class App extends Component {
  constructor(){
    super();
    this.state={
      nom:"",
      prenom:"",
      birthday:"",
      profil:""
    }
    this.handleChange= this.handleChange.bind(this)
    this.handleSubmit= this.handleSubmit.bind(this)
  }
  handleChange(e){
    console.log([e.target.name]);

    this.setState({ [e.target.name]: e.target.value });

  }
  handleSubmit(e){
    e.preventDefault()
    this.setState({ profil:this.state.nom
    });
    console.log(this.state.profil.nom)
  }
  render() {
    // function PDF1(){
    //   var doc = new jsPDF();
    //   var elementHandler = {
    //     '#ignorePDF': function (element, renderer) {
    //       return true;
    //     }
    //   };
    //   var source = $('#ffff').html();;
    //   doc.fromHTML(
    //     source,
    //     15,
    //     15,
    //     {
    //       'width': 180,'elementHandlers': elementHandler
    //     });

    //     doc.output("datauri");
    //     doc.save("datauri.pdf");
    //   }

    //   $( document ).ready(function() {
    //     //console.log( "ready!" );
    //     PDF1();
    //   });
    return (


          <div >
              <form onSubmit={this.handleSubmit}>
                <div class="form-group">
                  <label for="exampleInputEmail1">Nom</label>
                  <input type="text" class="form-control" id="exampleInputEmail1"  onChange={this.handleChange} value={this.state.nom} name="nom" aria-describedby="emailHelp" placeholder="Nom"/>
                </div>
                <div class="form-group">
                  <label for="exampleInputPassword1">Prenom</label>
                  <input type="text" class="form-control" id="exampleInputPassword1"  onChange={this.handleChange} value={this.state.prenom} name="prenom" placeholder="Prenom"/>
                </div>
                <div class="form-group">
                <label for="exampleInputPassword1">Date de Naissance </label>
                <input type="date" class="form-control" name="birthday"  value={this.state.birthday} onChange={this.handleChange} id="exampleInputPassword1" />
                </div>
              <button type="submit" class="btn btn-primary">Creer</button>
            </form>
            <div className="resultat">
            {this.state.nom}
            </div>
          </div>

    );
  }
}
export default App;
